check.run:
apt-get update && apt-get install -y jq
display_contents=$(cat << EOM
  [
    {
      "kind": "table",
      "name": "Example Table",
      "header": ["foo", "bar", "baz"],
      "data": [
        [0, 1, 2],
        [3, 4, 5],
        [6, 7, 8]
      ]
    },
    {
      "kind": "value",
      "name": "Counter value",
      "data": 0
    }
  ]
EOM
)
echo "${display_contents}" > /tmp/run/.display
